import handler from 'lib/handler';
import dbConnect from "lib/dbConnect";
import nextConnect from 'next-connect';
import { NextApiRequest, NextApiResponse } from 'next';
import { isAdminMiddleware } from 'middleware/checkUser';

import PublicTool from 'models/public-tool';
import PublicToolType from 'types/public-tool';

export default nextConnect(handler)
    // .use(isAdminMiddleware)
    .post(async (req: NextApiRequest, res: NextApiResponse) => {
        try {
            await dbConnect();
            // const { tools } = req.body;
            
          if(process.env.NODE_ENV === "development"){  if (!tools) {
                return res.status(400).json({ success: false, message: "Missing required fields" });
            }
            const sanitizedTools = await sanitize(tools) as PublicToolType[];
            // insert tool in bulk 

            await PublicTool.insertMany(sanitizedTools);}
            return res.status(200).json({ success: true, message: "Tool submitted successfully" });


        }
        catch (error) {
            console.log(error);
            return res.status(500).json({ success: false, message: error.message ?? "Internal Server Error" });
        }


    })

const sanitize = async(tools: any[]) => {

    return new Promise<PublicToolType[]>(async(resolve, reject) => {
        
        const result = tools.map(tool => {
            const categories = tool.toolTags.map((tag: string) => {
                return { 
                    name: tag.trim(),
                    slug: tag.toLocaleLowerCase().trim().split(" ").join("_")
                         
                     }
            }) || []
            return {
                name: tool.name,
                description: tool.desc,
                link: tool.url,
                coverImage: tool.imageSrc,
                slug: tool.slug,
                pricing_type: tool.pricingModel.toLocaleLowerCase().trim().split(" ").join("-") || "other",
                tags: tool.toolTags,
                categories: categories,
                status: "published",
                verified:true,
                createdAt: new Date(),
                author:{
                    name:"Kanak",
                    email:"kanakkholwal@gmail.com",
                    public_link:"https://kanakkholwal.eu.org"
                }
                
            } as PublicToolType
    });

        if(result){
            resolve(result);
        }
        else{
            reject("Error sanitizing tools");
        }

    });

}


